ds = item.biz_id            if (is_faved == 200)            {                var confirmed = confirm('取消收藏此店铺?');                if (confirmed)                {                    params.operation = 'delete';                    $.ajax({	                url : api_url + 'fav_biz/edit_certain',	                data : params,	                success : function(res)	                {	                    console.log(res);	                    $('.focus').text('关注');	                    $('.everfocus').css('color','#3E3A39');	                    $('.everfocus').eq(1).text('加关注');	                    	                }                    });                    is_faved = 0;                }            }            else            {                $.ajax({                url : api_url + 'fav_biz/create',                data : params,                success : function(res){                    $('.focus').text('已关注');                    $('.everfocus').css('color','#ff5171');                    $('.everfocus').eq(1).text('已关注');                }                });            }        })// TODO 使用一种方式排序时，更新其它排序方式状态var sold_overall = 0; // 是否已按销量倒序排序var time_publish = 0; // 是否已按上架时间倒序排序var by_price = 0; // 是否已按商城价倒序排序// 生成单品DOMfunction generate_dom_item(item){    if (item.length == 0) return;    var oLi = '<li>' +        '<a href="'+ page_item_detail+item.item_id +'">' +        '<img src="'+(item.url_image_main.indexOf('http') != -1? item.url_image_main: (item_image_root + item.url_image_main))+'">' +        '<h1>'+item.name+'</h1>' +        '<p><span>¥'+item.price+'</span></p>' +        '</a>' +        '</li>';    $('#bizDetailList').append(oLi);}$('.price_reset').on('click',function(){
    $('.price_min').val('');
    $('.price_max').val('');
})            });//下拉加载更多	var oitermId;	var page = 0, //分页码    	off_on = false,//分页开关(滚动加载方法 1 中用的)    	sstatus,     	timers = null;    var priceflag = 0; $(".shopindextextnav li").on('click',function(){ 		page = 0; 		$('.shang-sanjiao').css('color','#9fa0a0');       	$('.xia-sanjiao').css('color','#9fa0a0'); 		$('#bizDetailList').html(''); 		if($(this).text() == '综合'){ 			sstatus = $(this).text(); 			$('.content').scrollTop(0);	 		$('.bottomline').css('display','none');			LoadingDataFn(page,'','','');			 		} 		if($(this).text() == '销量'){ 			sstatus = $(this).text(); 			$('.content').scrollTop(0);	 		$('.bottomline').css('display','none');			LoadingDataFn(page,'DESC','',''); 		} 		if($(this).text() == '新品'){ 			sstatus = $(this).text(); 			$('.content').scrollTop(0);	 		$('.bottomline').css('display','none');			LoadingDataFn(page,'','DESC',''); 		} 			if($(this).text() == '价格'){ 				sstatus = $(this).text();			if(priceflag == 0){				LoadingDataFn(page,'','','DESC');				$(this).find('i').eq(0).css('color','#9fa0a0');				$(this).find('i').eq(1).css('color','#FF3649');				priceflag = 1;			}			else{				LoadingDataFn(page,'','','ASC');			$(this).find('i').eq(0).css('color','#FF3649');			$(this).find('i').eq(1).css('color','#9fa0a0');			priceflag = 0;			}		} 		 }); //筛选区域js $('.price_finish').on('click',function(){ 	sstatus = '筛选'; 	page = 0; 	$('.shang-sanjiao').css('color','#9fa0a0');    $('.xia-sanjiao').css('color','#9fa0a0'); 	$('#bizDetailList').html(''); 	LoadingDataFn(page,'','','',$('.price_min').val(),$('.price_max').val()); 	$('html,body').css('overflow','auto'); 	$(".screening").fadeOut(); 	 });//加载数据var LoadingDataFn = function(page,xl,xp,jg,minval,maxval) {      	  $.ajax({
        url : api_item_index,
        data : {app_type:'client',limit:10, biz_id:item.biz_id,offset:page,orderby_sold_overall:xl,orderby_time_publish:xp,orderby_price:jg,price_min:minval,price_max:maxval},
