            var con = res.content;            if(con.error){					$('.bottomline').css('display','block');					$('.bottomline').text('(づ￣3￣)づ╭❤～r人家是有底线的啦~');					page = 0;					off_on = true;					return;				};            for (var i = 0;i < con.length;i++) {
            	  var oLi = '<li>' +		        '<a href="'+ page_item_detail+con[i].item_id +'">' +		        '<img src="'+(con[i].url_image_main.indexOf('http') != -1? con[i].url_image_main: (item_image_root + con[i].url_image_main))+'">' +		        '<h1>'+con[i].name+'</h1>' +		        '<p><span>¥'+con[i].price+'</span></p>' +		        '</a>' +		        '</li>';		    $('#bizDetailList').append(oLi);
            }          
        }
    });    off_on = true; //[重要]这是使用了 {滚动加载方法1}  时 用到的 ！！！[如果用  滚动加载方法1 时：off_on 在这里不设 true的话， 下次就没法加载了哦！]};//滚动加载方法1$('.content').scroll(function() {    //当时滚动条离底部60px时开始加载下一页的内容    if (($(this)[0].scrollTop + $(this).height() + 60) >= $(this)[0].scrollHeight) {    	 clearTimeout(timers);        timers = setTimeout(function() {            	page+=10;            	$('.bottomline').css('display','block');		    	$('.bottomline').text('加载中...');		        if(sstatus == '综合' && off_on){		            LoadingDataFn(page,'','');  //调用执行上面的加载方法		        }		        if(sstatus == '销量' && off_on){		        	LoadingDataFn(page,'DESC','');		        }		        if(sstatus == '新品' && off_on){		        	LoadingDataFn(page,'DESC','DESC');		        }		        if(sstatus == '价格'){					if(priceflag == 1){						LoadingDataFn(page,'','','DESC');					}					else{						LoadingDataFn(page,'','','ASC');					}				}				if(sstatus == '筛选' && off_on){		        	LoadingDataFn(page,'','','',$('.price_min').val(),$('.price_max').val());		        }           }, 300);    }});function getUrlParam(name) {    var reg = new RegExp("(^|&)" + name + "=([^&]*)(&|$)"); // 构造一个含有目标参数的正则表达式对象    var r = window.location.search.substr(1).match(reg);  // 匹配目标参数    if (r != null) return unescape(r[2]); return null; // 返回参数值}$('.zonghe').click();$('.jinlaiChat').click(function(e){	e.preventDefault();	window.location.href = 'https://www.517ybang.com/chat/index?biz_id=' + getUrlParam('id');})</script>